        public Task<ICollection<PetroTable>> EPSGetAllPetroTablesByVersionAsync(int versionID)
        {
            if (versionID > 0)
            {
                ICollection<PetroTable> petroTableList = petroTable.Where(s => s.VersionID == versionID).ToList();
                return Task.Run(() => petroTableList);
            }
            else
                throw new Exception("Invalid VersionId");
        }

        Task<EPSTableErrorCodes> IEPSTableRepository.EPSUpsertPetroTableAsync(PetroTable _petroTable)
        {
            if (petroTable.Any(s => s.VersionID == _petroTable.VersionID && s.TableName == _petroTable.TableName && s.EffectiveDate == _petroTable.EffectiveDate))
            {
                var duplicateEntry = petroTable.Where(s => s.VersionID == _petroTable.VersionID && s.TableName == _petroTable.TableName && s.EffectiveDate == _petroTable.EffectiveDate).FirstOrDefault();
                petroTable.Remove(duplicateEntry);
            }
            petroTable.Add(_petroTable);
            return Task.Run(() => EPSTableErrorCodes.Succeeded);
        }
    }
